const std::string commandId = "CommandInputGallery";
const std::string commandName = "Command Input Gallery";
const std::string commandDescription = "Demo command input examples.";

// Create the command definition.
static Ptr<CommandDefinition> createCommandDefinition()
{
	Ptr<CommandDefinitions> commandDefinitions = ui->commandDefinitions();
	if (!commandDefinitions)
		return nullptr;

	// Be fault tolerant in case the command is already added.
	Ptr<CommandDefinition> cmDef = commandDefinitions->itemById(commandId);
	if (!cmDef)
	{
		std::string resourcePath = "./resources";
		cmDef = commandDefinitions->addButtonDefinition(commandId,
			commandName,
			commandDescription,
			resourcePath);// absolute resource file path is specified
	}

	return cmDef;
}

static void addRow(Ptr<TableCommandInput> tableInput)
{
	if (!tableInput)
		return;

	static size_t rowNumber = 0;
	std::stringstream rowId;
	rowId << rowNumber;
	Ptr<CommandInputs> tableChildInputs = tableInput->commandInputs();
	Ptr<CommandInput> childTableValueInput = tableChildInputs->addValueInput(tableInput->id() + "_value" + rowId.str(), "Value", "cm", ValueInput::createByReal(0.0));
	Ptr<CommandInput> childTableStringInput = tableChildInputs->addStringValueInput(tableInput->id() + "_string" + rowId.str(), "String", "1");
	Ptr<CommandInput> childTableSpinnerInput = tableChildInputs->addIntegerSpinnerCommandInput(commandId + "_spinnerInt" + rowId.str(), "Integer Spinner", 2, 9, 2, 2);

	int row = tableInput->rowCount();
	tableInput->addCommandInput(childTableValueInput, row, 0);
	tableInput->addCommandInput(childTableStringInput, row, 1);
	tableInput->addCommandInput(childTableSpinnerInput, row, 2);

	rowNumber = rowNumber + 1;
}
// InputChange event handler.
class OnInputChangedEventHander : public adsk::core::InputChangedEventHandler
{
public:
	void notify(const Ptr<InputChangedEventArgs>& eventArgs) override
	{
		Ptr<InputChangedEvent> event = eventArgs->firingEvent();
		if (!event)
			return;

		Ptr<Command> command = event->sender();
		if (!command)
			return;

		Ptr<CommandInput> cmdInput = eventArgs->input();
		if (!cmdInput)
			return;

		Ptr<CommandInputs> inputs = command->commandInputs();
		if (!inputs)
			return;

		Ptr<TableCommandInput> tableInput = inputs->itemById(commandId + "_table");
		if (!tableInput)
			return;

		if (cmdInput->id() == tableInput->id() + "_add") {
			addRow(tableInput);
		}
		else if (cmdInput->id() == tableInput->id() + "_delete") {
			if (tableInput->selectedRow() == -1) {
				ui->messageBox("Select one row to delete");
			}
			else {
				tableInput->deleteRow(tableInput->selectedRow());
			}
		}
	}
};

// CommandExecuted event handler.
class OnExecuteEventHander : public adsk::core::CommandEventHandler
{
public:
	void notify(const Ptr<CommandEventArgs>& eventArgs) override
	{

	}
};

// CommandDestroyed event handler
class OnDestroyEventHandler : public adsk::core::CommandEventHandler
{
public:
	void notify(const Ptr<CommandEventArgs>& eventArgs) override
	{
		adsk::terminate();
	}
};

// CommandCreated event handler.
class CommandCreatedEventHandler : public adsk::core::CommandCreatedEventHandler
{
public:
	void notify(const Ptr<CommandCreatedEventArgs>& eventArgs) override
	{
		if (eventArgs)
		{
			Ptr<Command> command = eventArgs->command();
			if (command)
			{
				Ptr<CommandEvent> onDestroy = command->destroy();
				if (!onDestroy)
					return;
				bool isOk = onDestroy->add(&onDestroyHandler_);
				if (!isOk)
					return;

				Ptr<InputChangedEvent> onInputChanged = command->inputChanged();
				if (!onInputChanged)
					return;
				isOk = onInputChanged->add(&onInputChangedHandler_);
				if (!isOk)
					return;

				Ptr<CommandInputs> inputs = command->commandInputs();
				if (!inputs)
					return;

				// Create tab input 1
				Ptr<TabCommandInput> tabCmdInput1 = inputs->addTabCommandInput(commandId + "_tab_1", "Tab 1");
				if (!tabCmdInput1)
					return;
				Ptr<CommandInputs> tab1ChildInputs = tabCmdInput1->children();
				if (!tab1ChildInputs)
					return;

				// Create readonly textbox input
				tab1ChildInputs->addTextBoxCommandInput(commandId + "_textBox", "Text Box", "This is an example of Text Box. It is readonly.", 2, true);

				// Create editable textbox input
				tab1ChildInputs->addTextBoxCommandInput(commandId + "_textBox", "Text Box2", "This is an example of Text Box. It is not readonly.", 2, false);

				// Create selection input
				Ptr<SelectionCommandInput> selectionInput = tab1ChildInputs->addSelectionInput(commandId + "_selection", "Select", "Basic select command input");
				if (!selectionInput)
					return;
				selectionInput->setSelectionLimits(0);

				// Create string value input
				tab1ChildInputs->addStringValueInput(commandId + "_string", "Text", "Basic string command input");

				// Create value input
				tab1ChildInputs->addValueInput(commandId + "_value", "Value", "cm", ValueInput::createByReal(0.0));

				// Create bool value input with checkbox style
				tab1ChildInputs->addBoolValueInput(commandId + "_checkbox", "Checkbox", true, "", false);

				// Create bool value input with button style
				tab1ChildInputs->addBoolValueInput(commandId + "_button", "Button", true, "resources", false);

				// Create float slider input with two sliders
				tab1ChildInputs->addFloatSliderCommandInput(commandId + "_rangeFloat", "Float Slider", "cm", 0, 10.0, true);

				// Create float slider input with two sliders and a value list
				std::vector<double> floatValueList;
				floatValueList.push_back(1.0);
				floatValueList.push_back(3.0);
				floatValueList.push_back(4.0);
				floatValueList.push_back(7.0);
				tab1ChildInputs->addFloatSliderListCommandInput(commandId + "_rangeFloat2", "Float Slider 2", "cm", floatValueList);

				// Create float slider input with two sliders and visible texts
				Ptr<FloatSliderCommandInput> floatSlider3 = tab1ChildInputs->addFloatSliderCommandInput(commandId + "_rangeFloat3", "Float Slider 3", "", 0, 50.0, false);
				if (!floatSlider3)
					return;
				floatSlider3->setText("Min", "Max");

				// Create integer slider input with one slider
				tab1ChildInputs->addIntegerSliderCommandInput(commandId + "_rangeInt", "Integer Slider", 0, 10);

				// Create integer slider input with two sliders and a value list
				std::vector<int> valueList;
				valueList.push_back(1);
				valueList.push_back(3);
				valueList.push_back(4);
				valueList.push_back(7);
				valueList.push_back(11);
				tab1ChildInputs->addIntegerSliderListCommandInput(commandId + "_rangeInt2", "Integer Slider 2", valueList);

				// Create float spinner input
				tab1ChildInputs->addFloatSpinnerCommandInput(commandId + "_spinnerFloat", "Float Spinner", "cm", 0.2 , 9.0 , 2.2, 1);

				// Create integer spinner input
				tab1ChildInputs->addIntegerSpinnerCommandInput(commandId + "_spinnerInt", "Integer Spinner", 2 , 9 , 2, 3);

				// Create dropdown input with checkbox style
				Ptr<DropDownCommandInput> dropdownInput = tab1ChildInputs->addDropDownCommandInput(commandId + "_dropdown", "Dropdown", DropDownStyles::CheckBoxDropDownStyle);
				if (!dropdownInput)
					return;
				Ptr<ListItems> dropdownItems = dropdownInput->listItems();
				if (!dropdownItems)
					return;
				dropdownItems->add("Item 1", false, "resources");
				dropdownItems->add("Item 2", false, "resources");

				// Create dropdown input with icon style
				Ptr<DropDownCommandInput> dropdownInput2 = tab1ChildInputs->addDropDownCommandInput(commandId + "_dropdown2", "Dropdown2", DropDownStyles::LabeledIconDropDownStyle);
				if (!dropdownInput2)
					return;
				Ptr<ListItems> dropdown2Items = dropdownInput2->listItems();
				if (!dropdown2Items)
					return;
				dropdown2Items->add("Item 1", true, "resources");
				dropdown2Items->add("Item 2", false, "resources");

				// Create dropdown input with radio style
				Ptr<DropDownCommandInput> dropdownInput3 = tab1ChildInputs->addDropDownCommandInput(commandId + "_dropdown3", "Dropdown3", DropDownStyles::LabeledIconDropDownStyle);
				if (!dropdownInput3)
					return;
				Ptr<ListItems> dropdown3Items = dropdownInput3->listItems();
				if (!dropdown3Items)
					return;
				dropdown3Items->add("Item 1", true, "");
				dropdown3Items->add("Item 2", false, "");

				// Create dropdown input with test list style
				Ptr<DropDownCommandInput> dropdownInput4 = tab1ChildInputs->addDropDownCommandInput(commandId + "_dropdown4", "Dropdown4", DropDownStyles::TextListDropDownStyle);
				if (!dropdownInput4)
					return;
				Ptr<ListItems> dropdown4Items = dropdownInput4->listItems();
				if (!dropdown4Items)
					return;
				dropdown4Items->add("Item 1", true, "");
				dropdown4Items->add("Item 2", false, "");

				// Create single selectable button row input
				Ptr<ButtonRowCommandInput> buttonRowInput = tab1ChildInputs->addButtonRowCommandInput(commandId + "_buttonRow", "Button Row", false);
				if (!buttonRowInput)
					return;
				Ptr<ListItems> buttonRowItems = buttonRowInput->listItems();
				if (!buttonRowItems)
					return;
				buttonRowItems->add("Item 1", false, "resources");
				buttonRowItems->add("Item 2", false, "resources");

				// Create multi selectable button row input
				Ptr<ButtonRowCommandInput> buttonRowInput2 = tab1ChildInputs->addButtonRowCommandInput(commandId + "_buttonRow2", "Button Row 2", true);
				if (!buttonRowInput2)
					return;
				Ptr<ListItems> buttonRow2Items = buttonRowInput2->listItems();
				if (!buttonRow2Items)
					return;
				buttonRow2Items->add("Item 1", false, "resources");
				buttonRow2Items->add("Item 2", false, "resources");

				// Create tab input 2
				Ptr<TabCommandInput> tabCmdInput2 = inputs->addTabCommandInput(commandId + "_tab_2", "Tab 2");
				if (!tabCmdInput2)
					return;
				Ptr<CommandInputs> tab2ChildInputs = tabCmdInput2->children();
				if (!tab2ChildInputs)
					return;

				// Create group input
				Ptr<GroupCommandInput> groupCmdInput = tab2ChildInputs->addGroupCommandInput(commandId + "_group", "Group");
				if (!groupCmdInput)
					return;
				groupCmdInput->isExpanded(true);
				groupCmdInput->isEnabledCheckBoxDisplayed(true);
				Ptr<CommandInputs> groupChildInputs = groupCmdInput->children();
				if (!groupChildInputs)
					return;

				// Create radio button group input
				Ptr<RadioButtonGroupCommandInput> radioButtonGroup = groupChildInputs->addRadioButtonGroupCommandInput(commandId + "_radioButtonGroup", "Radio button group");
				if (!radioButtonGroup)
					return;
				Ptr<ListItems> radioButtonItems = radioButtonGroup->listItems();
				if (!radioButtonItems)
					return;
				radioButtonItems->add("Item 1", false);
				radioButtonItems->add("Item 2", false);
				radioButtonItems->add("Item 3", false);

				// Create image input
				groupChildInputs->addImageCommandInput(commandId + "_image", "Image", "resources/image.png");

				// Create direction input 1
				Ptr<DirectionCommandInput> directionCmdInput = tab2ChildInputs->addDirectionCommandInput(commandId + "_direction", "Direction");
				if (!directionCmdInput)
					return;
				directionCmdInput->setManipulator(Point3D::create(0, 0, 0), Vector3D::create(1, 0, 0));

				// Create direction input 2
				Ptr<DirectionCommandInput> directionCmdInput2 = tab2ChildInputs->addDirectionCommandInput(commandId + "_direction2", "Direction 2", "resources");
				if (!directionCmdInput2)
					return;
				directionCmdInput2->setManipulator(Point3D::create(0, 0, 0), Vector3D::create(0, 1, 0)); 
				directionCmdInput2->isDirectionFlipped(true);

				// Create distance value input 1
				Ptr<DistanceValueCommandInput> distanceValueInput = tab2ChildInputs->addDistanceValueCommandInput(commandId + "_distanceValue", "DistanceValue", ValueInput::createByReal(2));
				if (!distanceValueInput)
					return;
				distanceValueInput->setManipulator(Point3D::create(0, 0, 0), Vector3D::create(1, 0, 0));
				distanceValueInput->minimumValue(0);
				distanceValueInput->isMinimumValueInclusive(true);
				distanceValueInput->maximumValue(10);
				distanceValueInput->isMaximumValueInclusive(true);

				// Create distance value input 2
				Ptr<DistanceValueCommandInput>  distanceValueInput2 = tab2ChildInputs->addDistanceValueCommandInput(commandId + "_distanceValue2", "DistanceValue 2", ValueInput::createByReal(1));
				if (!distanceValueInput2)
					return;
				distanceValueInput2->setManipulator(Point3D::create(0, 0, 0), Vector3D::create(0, 1, 0));
				distanceValueInput2->expression("1 in");
				distanceValueInput2->hasMinimumValue(false);
				distanceValueInput2->hasMaximumValue(false);

				// Create table input
				Ptr<TableCommandInput> tableInput = tab2ChildInputs->addTableCommandInput(commandId + "_table", "Table", 3, "1:1:1");
				addRow(tableInput);

				Ptr<CommandInput> addButtonInput = tab2ChildInputs->addBoolValueInput(tableInput->id() + "_add", "Add", false, "", true);
				tableInput->addToolbarCommandInput(addButtonInput);
				Ptr<CommandInput> deleteButtonInput = tab2ChildInputs->addBoolValueInput(tableInput->id() + "_delete", "Delete", false, "", true);
				tableInput->addToolbarCommandInput(deleteButtonInput);
			}
		}
	}
private:
	OnExecuteEventHander onExecuteHander_;
	OnDestroyEventHandler onDestroyHandler_;
	OnInputChangedEventHander onInputChangedHandler_;
} cmdCreated_;

extern "C" XI_EXPORT bool run(const char* context)
{
	app = Application::get();
	if (!app)
		return false;

	ui = app->userInterface();
	if (!ui)
		return false;

	Ptr<CommandDefinition> command = createCommandDefinition();
	if (!command)
		return false;

	Ptr<CommandCreatedEvent> commandCreatedEvent = command->commandCreated();
	if (!commandCreatedEvent)
		return false;
	commandCreatedEvent->add(&cmdCreated_);
	command->execute();

	// prevent this module from being terminate when the script returns, because we are waiting for event handlers to fire
	adsk::autoTerminate(false);

	return true;
}

