def mapJoint(self, compOcc, targetProf, source): self.body.copyToComponent(compOcc) self.bodyOcc = compOcc.bRepBodies.item(0) matchFace = findMatchFace(self.bodyOcc)
# Create the first joint geometry with the end face geo0 = adsk.fusion.JointGeometry.createByPlanarFace(matchFace.face, None, adsk.fusion.JointKeyPointTypes.CenterKeyPoint) # Create the second joint geometry with the sketch profile geo1 = adsk.fusion.JointGeometry.createByProfile(targetProf, None, adsk.fusion.JointKeyPointTypes.CenterKeyPoint) # Create joint input joints = self.rootComp.joints jointInput = joints.createInput(geo0, geo1) jointInput.angle = source.angle jointInput.isFlipped = True # Create the joint joint = joints.add(jointInput)
# check angle occurances and if not zero delete joint, combine angle with inital angle, and redo joint occOne = joint.occurrenceOne bodyOneProxy = occOne.bRepBodies.item(0) newSourceOne = findMatchFace(bodyOneProxy) occTwo = joint.occurrenceTwo bodyTwoProxy = occTwo.bRepBodies.item(0) newSourceTwo = findMatchFace(bodyTwoProxy) measureResult = self.app.measureManager.measureAngle(newSourceOne.edge, newSourceTwo.edge) angle = measureResult.value if angle != 0: print('>>>>> Angle between source and target {}'.format(angle)) joint.deleteMe() jointInput.angle = adsk.core.ValueInput.createByReal(source.angle.realValue - angle) joint = joints.add(jointInput)

# Create loft feature input loftFeats = rootComp.features.loftFeatures loftInput = loftFeats.createInput(adsk.fusion.FeatureOperations.NewBodyFeatureOperation) loftSectionsObj = loftInput.loftSections loftSectionsObj.add(openProfile1) loftSectionsObj.add(openProfile2) loftInput.isSolid = False
# Create loft feature loftFeats.add(loftInput)
except: if ui: ui.messageBox('Failed:\n{}'.format(traceback.format_exc()))

