import adsk.core, adsk.fusion, adsk.cam, traceback

def run(context):
    ui = None
    try:
        app = adsk.core.Application.get()
        ui  = app.userInterface

        # Make sure CAM is active.
        if not type(app.activeProduct) is adsk.cam.CAM:
            ui.messageBox('The CAM workspace must be active.')

        cam = adsk.cam.CAM.cast(app.activeProduct)
        des = adsk.fusion.Design.cast(app.activeDocument.products.itemByProductType("DesignProductType"))

        # Iterate through the setups.
        setup = adsk.cam.Setup.cast(None)
        for setup in cam.setups:
            # Get the first element that the setup references.
            model = setup.models.item(0)

            if type(model) is adsk.fusion.BRepBody:
                body = adsk.fusion.BRepBody.cast(model)

                # Get the parent component this body is in.
                parentComp = body.parentComponent
                if parentComp != des.rootComponent:
                    setup.name = parentComp.name                
            elif type(model) is adsk.fusion.Occurrence:
                occ = adsk.fusion.Occurrence.cast(model)
                setup.name = occ.component.name
    except:
        if ui:
            ui.messageBox('Failed:\n{}'.format(traceback.format_exc()))

extern "C" XI_EXPORT bool run(const char* context)
{
	app = Application::get();
	if (!app)
		return false;

	ui = app->userInterface();
	if (!ui)
		return false;

	Ptr<Design> des = app->activeProduct();
	Ptr<Component> root = des->rootComponent();

	Ptr<Selection> select = ui->selectEntity("Select the target body.", "Bodies");
	Ptr<BRepBody> targetBody = select->entity();
	adsk::doEvents();

	select = ui->selectEntity("Select the tool body.", "Bodies");
	Ptr<BRepBody> toolBody = select->entity();
	Ptr<ObjectCollection> tools = adsk::core::ObjectCollection::create();
	tools->add(toolBody);

	Ptr<CombineFeatures> combines = root->features()->combineFeatures();
	Ptr<CombineFeatureInput> combineInput = combines->createInput(targetBody, tools);
	combineInput->isKeepToolBodies(true);
	combineInput->isNewComponent(true);
	combineInput->operation(adsk::fusion::FeatureOperations::CutFeatureOperation);
	Ptr<CombineFeature> combine = combines->add(combineInput);
}

def compareComponents():
    ui = None

    try:
        app = adsk.core.Application.get()
        ui = app.userInterface
        product = app.activeProduct
        design = adsk.fusion.Design.cast(product)

        compList = []
        occList = []
        occ = adsk.fusion.Occurrence.cast(None)
        for occ in design.rootComponent.occurrences:
            foundComp = False
            comp = adsk.fusion.Component.cast(None)
            for comp in compList:
                if comp == occ.component:
                    foundComp = True
                    break

            if not foundComp:
                occList.append(occ)
                compList.append(occ.component)

        result = ''
        for occ in occList:
            result += occ.name + '\n'

        ui.messageBox(result)
    except:  
        if ui:
            ui.messageBox('Failed:\n{}'.format(traceback.format_exc()))

