bool BoxJoint::isPointOnSurface(const Ptr<Point3D>& testPoint, const Ptr<BRepFace>& plane)
{
    bool isOk = false;
    Ptr<Point3D> planePoint;

    Ptr<SurfaceEvaluator> surfaceEval = plane->evaluator();
    Ptr<Point2D> param;

    isOk = surfaceEval->getParameterAtPoint(testPoint, param);
    if (!isOk) return false;

    isOk = surfaceEval->getPointAtParameter(param, planePoint);
    if (!isOk) return false;

    XTRACE(L"test point : (%lf, %lf, %lf)\n", testPoint->x(), testPoint->y(), testPoint->z());
    XTRACE(L"plane point : (%lf, %lf, %lf)\n", planePoint->x(), planePoint->y(), planePoint->z());

    if (testPoint->isEqualTo(planePoint))
    {

		{
			return true;
		}
		else
		{
			return false;
		}
    }
    else
    {
        return false;
    }
}

def exportCompBodyAsSTL():
    app = adsk.core.Application.get()
    ui = app.userInterface
    try:
        product = app.activeProduct
        design = adsk.fusion.Design.cast(product)
        exportMgr = design.exportManager
        outDir = "c:/temp/fusion1"

        for comp in design.allComponents:
            for body in comp.bRepBodies:
                fileName = outDir + "/" + comp.name.replace(" ","_" ) + body.name

                # create stl exportOptions
                stlExportOptions = exportMgr.createSTLExportOptions(body, fileName)
                stlExportOptions.sendToPrintUtility = False

                exportMgr.execute(stlExportOptions)
    except:
        if ui:
            ui.messageBox('Failed:\n{}'.format(traceback.format_exc()))

def exportCompBodyAsSTL2():
    app = adsk.core.Application.get()
    ui = app.userInterface
    try:
        product = app.activeProduct
        design = adsk.fusion.Design.cast(product)
        root = design.rootComponent
        exportMgr = design.exportManager
        outDir = "c:/temp/fusion1"

        for comp in design.allComponents:
            if comp != root:
                # Find any occurrence using this component.
                occs = root.allOccurrencesByComponent(comp)
                if occs.count > 0:
                    occ = occs.item(0)

            for body in comp.bRepBodies:
                if comp != root:
                    # Create a body proxy.
                    body = body.createForAssemblyContext(occ)

                fileName = outDir + "/" + comp.name.replace(" ","_" ) + body.name

                # create stl exportOptions
                stlExportOptions = exportMgr.createSTLExportOptions(body, fileName)
                stlExportOptions.sendToPrintUtility = False

                exportMgr.execute(stlExportOptions)
    except:
        if ui:
            ui.messageBox('Failed:\n{}'.format(traceback.format_exc()))
